// src/data/categories.ts

export interface Category {
  id: string;
  name: string;
  count: number;
  isNew?: boolean;
  subcategories?: Category[];
}

export const categories: Category[] = [
  {
    id: 'technology',
    name: 'Technology',
    count: 1,
    subcategories: [
      { id: 'ai', name: 'Artificial Intelligence', count: 1 }
    ]
  },
  {
    id: 'entertainment',
    name: 'Entertainment',
    count: 2,
    subcategories: [
      { id: 'movies', name: 'Movies', count: 2 }
    ]
  },
  {
    id: 'photography',
    name: 'Photography',
    count: 2
  },
  {
    id: 'travel',
    name: 'Travel',
    count: 1
  },
  {
    id: 'finance',
    name: 'Finance',
    count: 1,
    subcategories: [
      { id: 'cryptocurrency', name: 'Cryptocurrency', count: 1 }
    ]
  },
  {
    id: 'food',
    name: 'Food & Cuisine',
    count: 1
  }
];

export function getCategoryById(id: string, categoryList: Category[] = categories): Category | undefined {
  for (const category of categoryList) {
    if (category.id === id) {
      return category;
    }
    if (category.subcategories) {
      const found = getCategoryById(id, category.subcategories);
      if (found) return found;
    }
  }
  return undefined;
}
