// src/data/posts.ts

export interface BasePost {
  id: string;
  title: string;
  type: 'essay' | 'movie' | 'image' | 'carousel';
  categoryId: string;
  votes: number;
}

export interface EssayPost extends BasePost {
  type: 'essay';
  content: string;
}

export interface MoviePost extends BasePost {
  type: 'movie';
  videoUrl: string;
  description: string;
}

export interface ImagePost extends BasePost {
  type: 'image';
  imageUrl: string;
  description: string;
}

export interface CarouselPost extends BasePost {
  type: 'carousel';
  images: { url: string; caption: string }[];
}

export type Post = EssayPost | MoviePost | ImagePost | CarouselPost;

export const examplePosts: Post[] = [
  {
	id: '1',
	title: 'The Future of AI',
	type: 'essay',
	categoryId: 'technology',
	votes: 120,
	content: 'Artificial Intelligence has come a long way in recent years...'
  },
  {
	id: '2',
	title: 'Inception',
	type: 'movie',
	categoryId: 'movies',
	votes: 95,
	videoUrl: 'https://example.com/inception-trailer',
	description: 'A mind-bending thriller about dreams within dreams.'
  },
  {
	id: '3',
	title: 'Sunset at the Beach',
	type: 'image',
	categoryId: 'photography',
	votes: 87,
	imageUrl: 'https://example.com/sunset-beach.jpg',
	description: 'A breathtaking sunset captured at a tropical beach.'
  },
  {
	id: '4',
	title: 'Wonders of the World',
	type: 'carousel',
	categoryId: 'travel',
	votes: 105,
	images: [
	  { url: 'https://example.com/taj-mahal.jpg', caption: 'Taj Mahal, India' },
	  { url: 'https://example.com/machu-picchu.jpg', caption: 'Machu Picchu, Peru' },
	  { url: 'https://example.com/petra.jpg', caption: 'Petra, Jordan' }
	]
  }
];

export function getPostsByCategoryId(categoryId: string): Post[] {
  return examplePosts.filter(post => post.categoryId === categoryId);
}
