import React from 'react';
import { Category, Link } from './data/categories';
import './animations.css';

interface CategoryPageProps {
  category: Category;
  onNavigate: (category: Category) => void;
}

const CategoryPage: React.FC<CategoryPageProps> = ({ category, onNavigate }) => {
  return (
	<div className="category-page">
	  {category.subcategories && (
		<div className="subcategory-list">
		  {category.subcategories.map((sub: Category, index: number) => (
			<div
			  key={index}
			  className="category-item fade-in"
			  style={{ animationDelay: `${index * 0.1}s` }}
			  onClick={() => onNavigate(sub)}
			>
			  {sub.name} <span className="count">({sub.count})</span>
			  {sub.isNew && <img src="/new.gif" alt="New" className="new-badge" />}
			</div>
		  ))}
		</div>
	  )}
	  {category.links && (
		<div className="link-list">
		  {category.links.map((link: Link, index: number) => (
			<a
			  key={index}
			  href={link.url}
			  className="category-item fade-in"
			  style={{ animationDelay: `${index * 0.1}s` }}
			>
			  {link.name}
			</a>
		  ))}
		</div>
	  )}
	</div>
  );
};

export default CategoryPage;
