import React from 'react';
import { Link } from 'react-router-dom';
import { getCategoryById, Category } from './data/categories';

interface CategoryPageProps {
  category: string;
  onNavigate: (categoryId: string) => void;
}

const CategoryPage: React.FC<CategoryPageProps> = ({ category, onNavigate }) => {
  const currentCategory = getCategoryById(category);

  if (!currentCategory) {
	return <div>Category not found</div>;
  }

  return (
	<div className="category-page">
	  <h2>{currentCategory.name}</h2>
	  {currentCategory.subcategories && (
		<div className="categories">
		  <ul>
			{currentCategory.subcategories.map((subCategory: Category) => (
			  <li key={subCategory.id} onClick={() => onNavigate(subCategory.id)}>
				<Link to={`/category/${subCategory.id}`}>{subCategory.name}</Link>
				<span className="count">({subCategory.count})</span>
				{subCategory.isNew && <img src="/new.gif" alt="New" className="new" />}
			  </li>
			))}
		  </ul>
		</div>
	  )}
	  {currentCategory.links && (
		<div className="links">
		  <ul>
			{currentCategory.links.map((link, index) => (
			  <li key={index}>
				<a href={link.url}>{link.name}</a>
			  </li>
			))}
		  </ul>
		</div>
	  )}
	</div>
  );
};

export default CategoryPage;
