import React from 'react';
import { Link } from 'react-router-dom';

const InteractiveMenu: React.FC = () => {
  // Assume the original image is 400x20 pixels
  // Adjust these values to match your actual image dimensions
  const imageWidth = 400;
  const imageHeight = 20;

  const menuStyle: React.CSSProperties = {
	position: 'relative',
	width: '100%',
	maxWidth: `${imageWidth}px`,
	aspectRatio: `${imageWidth} / ${imageHeight}`,
  };

  const buttonStyle: React.CSSProperties = {
	position: 'absolute',
	background: 'transparent',
	border: 'none',
	cursor: 'pointer',
	top: 0,
	height: '100%',
  };

  const menuItems = [
	{ name: 'Top', path: '/top', left: '0%', width: '16.67%' },
	{ name: 'Up', path: '/up', left: '16.67%', width: '16.67%' },
	{ name: 'Search', path: '/search', left: '33.33%', width: '16.67%' },
	{ name: 'Mail', path: '/mail', left: '50%', width: '16.67%' },
	{ name: 'Add', path: '/add', left: '66.67%', width: '16.67%' },
	{ name: 'Help', path: '/help', left: '83.33%', width: '16.67%' },
  ];

  return (
	<div style={menuStyle}>
	  <img 
		src="/menu.gif" 
		alt="Menu" 
		className="menu" 
		style={{
		  width: '100%',
		  height: '100%',
		  objectFit: 'contain',
		  imageRendering: 'pixelated',
		}} 
	  />
	  {menuItems.map((item) => (
		<Link
		  key={item.name}
		  to={item.path}
		  style={{
			...buttonStyle,
			left: item.left,
			width: item.width,
		  }}
		  title={item.name}
		/>
	  ))}
	</div>
  );
};

export default InteractiveMenu;
