import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getCategoryById, Category } from './data/categories';
import PostList from './PostList';
import './animations.css'; // Make sure to import the CSS file

interface CategoryPageProps {
  category: string;
  onNavigate: (categoryId: string) => void;
}

interface Post {
  id: string;
  title: string;
  url: string;
  votes: number;
}

const CategoryPage: React.FC<CategoryPageProps> = ({ category, onNavigate }) => {
  const [currentCategory, setCurrentCategory] = useState<Category | null>(null);
  const [posts, setPosts] = useState<Post[]>([]);

  useEffect(() => {
	const fetchedCategory = getCategoryById(category);
	setCurrentCategory(fetchedCategory || null);

	if (fetchedCategory && !fetchedCategory.subcategories) {
	  fetchPosts(category).then(setPosts);
	} else {
	  setPosts([]);
	}
  }, [category]);

  const fetchPosts = async (categoryId: string): Promise<Post[]> => {
	// Simulating an API call
	return new Promise((resolve) => {
	  setTimeout(() => {
		resolve([
		  { id: '1', title: 'First post in this category', url: 'https://example.com/post1', votes: 10 },
		  { id: '2', title: 'Another interesting post', url: 'https://example.com/post2', votes: 8 },
		  // Add more mock posts here
		]);
	  }, 300);
	});
  };

  if (!currentCategory) {
	return <div>Category not found</div>;
  }

  return (
	<div className="category-page fade-in">
	  <h2>{currentCategory.name}</h2>
	  {currentCategory.subcategories ? (
		<div className="subcategories">
		  <ul>
			{currentCategory.subcategories.map((subCategory: Category) => (
			  <li key={subCategory.id} className="category-item fade-in" onClick={() => onNavigate(subCategory.id)}>
				<Link to={`/category/${subCategory.id}`}>{subCategory.name}</Link>
				<span className="count">({subCategory.count})</span>
				{subCategory.isNew && <img src="/new.gif" alt="New" className="new-badge" />}
			  </li>
			))}
		  </ul>
		</div>
	  ) : (
		<PostList posts={posts} />
	  )}
	</div>
  );
};

export default CategoryPage;
